    /*}}}*/
/*
console.log("@@@")
console.log("@@@ log_msg=["+log_msg+"]")
for(let i=0; i < args.length; ++i) console.log("@@@ args["+i+"]=["+args[i]+"]");
console.log("@@@")
*/
