/*_ s_escapeREGEX {{{*/
/*{{{*/
var s_escapeREGEX_chars
    = {  "\\" : "\\\\"

        ,"^"  :  "\\^"
        ,"$"  :  "\\$"
        ,"."  :  "\\."
        ,"+" :  "\\+"
        ,"*" :  "\\*"

        ,"("  :  "\\("
        ,")"  :  "\\)"
        ,"["  :  "\\["
        ,"]"  :  "\\]"
    };

let regexp_REGEX_CHARS = new RegExp("[\\\\"+"^$"+"\.\+\*"+"\\(\\)\\[\\]]", "g");
/*}}}*/
let s_escapeREGEX = function (text)
{
  return text.replace(regexp_REGEX_CHARS, function(character) { return s_escapeREGEX_chars[character]; });
};
