    /*}}}*/
    /* [innerHTML] {{{*/
/*
  _cleanup_pat_bag();
  _cleanup_div(off_bag);
  _cleanup_div(bak_bag);
*/

    let pat_bag_innerHTML = "";
    let off_bag_innerHTML = "";
    let bak_bag_innerHTML = "";
