/*_ get_thumb_p_str {{{*/
let get_thumb_p_str = function(thumb_p)
{
    if(thumb_p < 0) return "";

    let thumb_i = Math.trunc(       thumb_p           );
    let thumb_f = Math.round(100 * (thumb_p - thumb_i)); /* 2 decimals */

    thumb_i = mPadStart(thumb_i,2," ");
    thumb_f = mPadStart(thumb_f,2,"0");

    return (thumb_i +"."+ thumb_f);
};
