        /* TRANSCRIPTS POSITION .. (when not pinned) */
        if(!transcript1.classList.contains(CSS_PINNED) ) transcript1.style.position = p;
        if(!transcript2.classList.contains(CSS_PINNED) ) transcript2.style.position = p;

