    /* SPREAD GROUPED TOOLS {{{*/
    if( !hiding_tools ) {
        t_tool_spread_xy(caller, x, y);

    }
    /*}}}*/
    /* LAYOUT OR HIDE TOOLS {{{*/
    let x_sign = (x < pivotXY.x) ? 1 : -1;
    let y_sign = (y < pivotXY.y) ? 1 : -1;
