let log_json = function(val,lxx)
{
    if(val == null     ) return "null";
    if(val == undefined) return "undefined";
    if(val.id          ) return "#"+val.id+(val.className ? ("."+val.className.replace(/ /g,".")) : "");
    if(val.tagName     ) return     val.tagName;

    let result = "";
    try {
        result
            = JSON.stringify(val);

        if( result.length > 64)
            result
                = result
                .   replace(regexp_URL_64, '"url":"$1..."')
        ;
        result
            = result
            . replace(regexp_BRACES , "")
            . replace(regexp_QUOTE  , "")
            . replace(regexp_BSLASH , "")
            . trim()
        ;

        result
            = (". "+result)
            .  replace(regexp_COMMA    ,        LF_HEAD+". ")
        ;

        result
            = result
            .  replace(/\. *([^:]*):{/g, ".$1:"+LF_HEAD+". ")
            .  replace(/}/g            , ""                 )
        ;

        result
            =(LF_HEAD+ result)
            .  replace(/^( *\. *)([^:]*):(.*)$/gm, mpad)
        ;

        result
            = LF_HEAD+"â”Œâ”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”"
            +          result
            + LF_HEAD+"â””â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”˜"
        ;

    } catch(ex) { result = LF+ex.message; }

    if( lxx )
        result = "%c"+ result;

    return result;
};

