    let      shadow_root = get_shadow_root();           let why_not = "";                 let lfx = lf8;


    if     ( node                == document.body.parentElement   ) { why_not = "HTML"                ;            }
    else if( node                == document.body                 ) { why_not = "BODY"                ;            }
