if( log_this && (why_not != "") && (lfx != lf8)) log("%c"+caller+"%c"+why_not+"%c"+get_n_lbl(node)
                                                     ,lbb+lbH+lfx,lbL+lfx     ,lbR+lfX[node.id ? 3:8]);
