let string_to_utf8 = function(s)
{
    let result      = "";
    let non_ascii;
    for(let       i = 0; i < s.length; ++i)
    {
        let      cp = s.codePointAt( i );
        let       u = (cp < 128) ? s[i] : "&#x"+cp.toString(16)+";";
        result += u;

        non_ascii = non_ascii || (cp >= 128);
    }


    return result;
};


let string_to_ascii = function(s)
{
    let non_ascii;
    let result = "";
    for(let  i = 0; i < s.length; ++i)
    {
        if(               (s[i  ] == "&")
           &&             (s[i+1] == "#"))
        {
            non_ascii   =  true;
            let hex_fmt = (s[i+2] == "x");

            let      ss =  s.substr(i);
            let     id0 =  hex_fmt ? 3 : 2;
            let     id1 =  ss.indexOf(";");
            let     str =  ss.substr(id0, id1-id0);
            let      cp =  parseInt(str, hex_fmt ? 16 : 10)
                ||         parseInt(str,           16     );

            let       c =  String.fromCodePoint( cp );
            result     +=  c;

            i          += id1;


        }
        else {
            result += s[i];
        }
    }


    return result;
};






