    if(event_LOG_TOOLTIP_timer) clearTimeout(event_LOG_TOOLTIP_timer);
    event_LOG_TOOLTIP_timer   = setTimeout(function() { event_LOG_TOOLTIP_handler(_caller); }, EVENT_LOG_TOOLTIP_DELAY);


